query:
select count(*)
 from match_fact as t1
 where t1.penalty = 'true' or t1.missed_penalty = 'true';

SELECT T2.player_name, count(*) FROM match_fact AS T1 JOIN player AS T2 ON T1.player_id = T2.player_id
 WHERE T1.goal = 'true' and T2.player_name ilike '%Giroud%'
 group by T2.player_name;

select distinct stadium.stadium_name
 from stadium join match on stadium.stadium_id = match.stadium_id
 where match.stage = 'Semi-finals' and match.year = 2002;

SELECT DISTINCT teamname FROM national_team WHERE teamname
NOT IN (SELECT teamname FROM national_team AS T1 JOIN world_cup as T2 ON T2.winner = T1.team_id
);

SELECT COUNT(*)
FROM match AS T1
WHERE T1.away_team_goals = 0 and T1.home_team_goals = 0;
