create table match
(
    match_id                  integer not null
        constraint match_pk
            primary key,
    year                      integer not null
        constraint match_world_cup_fk
            references world_cup,
    round_id                  integer,
    datetime                  timestamp,
    stage                     varchar,
    win_conditions            varchar,
    referee                   varchar,
    assistant_1               varchar,
    assistant_2               varchar,
    attendance                integer,
    stadium_id                integer
        constraint match_stadium_fk
            references stadium,
    did_home_win              boolean,
    is_draw                   boolean,
    home_team_id              integer
        constraint match_national_team_home_team_id_team_id__fk
            references national_team,
    away_team_id              integer
        constraint match_national_team_away_team_id_team_id_fk
            references national_team,
    half_time_home_goals      integer,
    home_team_goals           integer,
    half_time_away_team_goals integer,
    away_team_goals           integer
);

alter table match
    owner to postgres;

create index match_attendance_idx
    on match (attendance);

create index match_datetime_idx
    on match (datetime);

create index match_match_id_idx
    on match (match_id);

create index match_round_id_idx
    on match (round_id);

create index match_stadium_id_idx
    on match (stadium_id);

create index match_year_idx
    on match (year);

grant select on match to inode_readonly;

