create table stadium
(
    stadium_id   integer not null
        constraint stadium_pk
            primary key,
    stadium_name varchar,
    city         varchar,
    capacity     integer,
    country      varchar,
    continent    varchar
);

alter table stadium
    owner to postgres;

create index stadium_capacity_idx
    on stadium (capacity);

create index stadium_stadium_id_idx
    on stadium (stadium_id);

grant select on stadium to inode_readonly;

