query:
SELECT DISTINCT teamname FROM national_team WHERE teamname
                NOT IN (SELECT teamname FROM world_cup_result join national_team ON world_cup_result.team_id = national_team.team_id
                WHERE prize = 'winner');

select count(*)
 from match_fact as t1
 where t1.penalty = 'true' or t1.missed_penalty = 'true';

SELECT T2.player_name, count(*) FROM match_fact AS T1 JOIN player AS T2 ON T1.player_id = T2.player_id
 WHERE T1.goal = 'true' and T2.player_name ilike '%Giroud%'
 group by T2.player_name;
