create table plays_as_away
(
    match_id                  integer not null
        constraint match_away_team_match_match_id_fk
            references match,
    team_id                   integer not null
        constraint match_away_team_team_id_fkey
            references national_team,
    half_time_away_team_goals integer,
    away_team_goals           integer
);

alter table plays_as_away
    owner to postgres;

