query:
SELECT teamname from national_team as t1
join world_cup_result as t2 on t1.team_id = t2.team_id
where t2.year = 2002 and t2.winner = 'true';

select sum(t1.goals), t1.teamname
from national_team as t1
group by t1.teamname order by sum(t1.goals) desc limit 1;

select t1.teamname, t1.goals, t1.year
from national_team as t1
where t1.teamname = 'Switzerland';

select count(*)
from match_fact as t1
where t1.penalty = 'true' or t1.missed_penalty = 'true';
