create table national_opponent_team
(
    team_id       integer not null
        constraint national_opponent_team_pk
            primary key,
    teamname      varchar,
    team_initials varchar(3),
    goals         integer,
    year          integer
);

alter table national_opponent_team
    owner to postgres;

create index national_opponent_team_goals_idx
    on national_opponent_team (goals);

create index national_opponent_team_team_id_idx
    on national_opponent_team (team_id);

create index national_opponent_team_year_idx
    on national_opponent_team (year);

grant select on national_opponent_team to inode_readonly;

