create table plays_match
(
    team_id                       integer not null
        constraint match_national_team_fk
            references national_team,
    team_role                     text,
    opponent_team_id              integer not null
        constraint match_national_opponent_team_fk
            references national_opponent_team,
    match_id                      integer not null,
    half_time_team_goals          integer,
    team_goals                    integer,
    half_time_opponent_team_goals integer,
    opponent_team_goals           integer,
    year                          integer not null
        constraint match_world_cup_fk
            references world_cup,
    round_id                      integer,
    datetime                      timestamp,
    stage                         varchar,
    win_conditions                varchar,
    referee                       varchar,
    assistant_1                   varchar,
    assistant_2                   varchar,
    attendance                    integer,
    stadium_id                    integer
        constraint match_stadium_fk
            references stadium,
    match_team_id                 varchar not null
        constraint match_pk
            primary key,
    did_win                       boolean,
    is_draw                       boolean
);

alter table plays_match
    owner to postgres;

create index match_attendance_idx
    on plays_match (attendance);

create index match_datetime_idx
    on plays_match (datetime);

create index match_half_time_opponent_team_goals_idx
    on plays_match (half_time_opponent_team_goals);

create index match_half_time_team_goals_idx
    on plays_match (half_time_team_goals);

create index match_match_id_idx
    on plays_match (match_id);

create index match_opponent_team_goals_idx
    on plays_match (opponent_team_goals);

create index match_opponent_team_id_idx
    on plays_match (opponent_team_id);

create index match_round_id_idx
    on plays_match (round_id);

create index match_stadium_id_idx
    on plays_match (stadium_id);

create index match_team_goals_idx
    on plays_match (team_goals);

create index match_team_id_idx
    on plays_match (team_id);

create index match_year_idx
    on plays_match (year);

grant select on plays_match to inode_readonly;

